sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    //Declare global variables
	var response_body = null;
    var req = request.body.data;

	var table_name = req.sn_table_name;

	if(!table_name){
		table_name = "sys_user";
	}

	var record = new GlideRecord(table_name);
	var first_name = req.sn_first_name;
	var last_name = req.sn_last_name;
	var email = req.sn_email;
	var custom_query = req.sn_custom_query;

	if(first_name && last_name){
		record.addQuery("first_name", fname);
		record.addQuery("last_name", lname);
		record.query();
		record.next();
		response_body["sys_id"] = record.getValue('sys_id');
	}
	else if (email){
		record.addQuery("first_name", fname);
		record.query();
		record.next();
		response_body["sys_id"] = record.getValue('sys_id');
	}
	else if (custom_query){
		record.addQuery(custom_query);
		record.query();
		record.next();
		response_body["sys_id"] = record.getValue('sys_id');
	}

	response.setBody(response_body);

	return response;

})(request, response);
